mapper.select:
SELECT id,title,job_type,location,
min_salary,
max_salary,
company_name,recruiter_name,create_id,job_desc,
is_alumni
FROM job


        AND status = #{status}


        AND title like CONCAT('%',#{title},'%')


        AND job_type = #{jobType}


        AND location like #{location}


        AND min_salary >= #{minSalary}


        AND max_salary <= #{maxSalary}


        AND id in
        #{id}


order by publish_time desc

SELECT id,title,job_type,job_desc,location,company_name,create_id,publish_time,
min_salary,max_salary
FROM job


        AND id = #{id}


        AND status = #{status}

SELECT id,title,job_type,location,company_name, min_salary, max_salary, recruiter_name,status,job_desc, FROM job
AND create_id = #{createId}
AND status = #{status}
order by update_time desc

SELECT job.id,title,job_type,location, min_salary, max_salary, company_name,recruiter_name,create_id,job_desc, group_concat(distinct jc.category_id) as category_id, is_alumni, publish_time FROM job JOIN job_category jc on job.id = jc.job_id
AND status = #{status}
AND job_type = #{jobType}
AND min_salary >= #{minSalary}
AND max_salary <= #{maxSalary}
AND jc.category_id in #{categoryId}
group by job.id
order by is_alumni desc,publish_time desc
