mapper.select:
SELECT title,job_type,location,
CAST(SUBSTRING_INDEX(salary_range, '-', 1) AS UNSIGNED) AS min_salary,
CAST(SUBSTRING_INDEX(salary_range, '-', -1) AS UNSIGNED) AS max_salary
FROM job


        AND title like CONCAT('%',#{title},'%')


        AND job_type = #{jobType}


        AND location like #{location}


        AND CAST(SUBSTRING_INDEX(salary_range, '-', -1) AS UNSIGNED) >= #{minSalary}


        AND CAST(SUBSTRING_INDEX(salary_range, '-', 1) AS UNSIGNED) <= #{maxSalary}
